function searchAnswer(x, i) {
let newHtml=x[i].innerHTML.toString();
newHtml=newHtml.replace(new RegExp(',',"g"),'，');
newHtml=newHtml.replace(new RegExp('\n',"g"),'');
newHtml=newHtml.replace(new RegExp(' ',"g"),'');

/*
newHtml=newHtml.replace(new RegExp('"',"g"),'');
newHtml=newHtml.replace(new RegExp("'","g"),'');
newHtml=newHtml.replace(new RegExp('“',"g"),'');
newHtml=newHtml.replace(new RegExp('”',"g"),'');
*/

let questionArr=newHtml.match('<span>(.+?)</span>');
if(questionArr===null)return;
let typeArr=newHtml.match('<spanclass="q-type".*?>(.*?)题.*?</span>');
if(typeArr===null)return;

let find=csvArr.indexOf(questionArr[1]);
if(find==-1)x[i].innerHTML+='<span style="color: #f7f7f7;">无</span>';
else x[i].innerHTML+='<span style="color: #f7f7f7;">'+csvArr[find+1]+'</span>';


}


